mapper.update:
update ${table} set 
			
				contentImage = #{fileName} where contentImageNum = #{imageNum}
			
			
				thumbnailImage = #{fileName} where thumbnailNum = #{imageNum}

mapper.select:
  		select 
  					
  						 thumbnailImage
  					
  					
  						contentImage
  					
  		from ${table} where
					
  						thumbnailNum = #{imageNum}
  					
  					
  						contentImageNum = #{imageNum}

mapper.delete:
delete from 
				 proejct.thumbnail
			
			
				proejct.contentimage
			
		where 
			
				thumbnailNum = #{imageNum}
			
			
				contentImageNum = #{imageNum}

mapper.update:
update user set
	
		name = #{user.name}
	
	
		phone = #{user.phone}
	
	
		birthday = #{user.birthday}
	
	
		gender = #{user.gender}
	
	
		email = #{user.email}
	
	
		isDel = #{user.isDel}
		, isDelDate = #{user.isDelDate}
	
	
		isDel = #{user.isDel}
	
	
		comment = #{user.comment}
	
	where id = #{user.id}

mapper.select:
select * from purchase
where invoice like concat ('%',#{rri.search},'%')
order by situation desc, deposit asc, depositDate asc, num desc limit #{rri.pageStart}, #{rri.perPageNum}

mapper.update:
update purchase set situation = #{purchase.situation}
					
						,invoice = null
					
					
						,confirmDate = #{purchase.confirmDate}
					
					
						,confirmDate = null
					
 					where num = #{purchase.num}
